data.value:
[
  {{#repeat 10}}
    {
      "id": {{add @index 1}},
      "name": "{{faker 'commerce.product'}}",
      "description": "{{faker 'commerce.productDescription'}}",
      "rate": {{int 1 5}},
      "image": "https://source.unsplash.com/random",
      "prices": [
        {
          "type": "default",
          "value": {{faker 'datatype.float' min=10 max=5000 precision=0.01}}
          {{#if (eq 1 (int 1 2))}}
            ,"discount": {{int 1 25}}
          {{/if}}
        },
        {
          "type": "pix",
          "value": {{faker 'datatype.float' min=10 max=5000 precision=0.01}}
        },
        {
          "type": "cartão de crédito",
          "value": {{faker 'datatype.float' min=10 max=5000 precision=0.01}},
          "installment": {{int 1 10}},
          "interest": false
        },
        {
          "type": "cartão de crédito",
          "value": {{faker 'datatype.float' min=10 max=5000 precision=0.01}},
          "installment": {{int 1 10}},
          "interest": true
        }
      ],
      "technicalDescription": {
        "brand": "{{faker 'company.name'}}",
        "model": "{{faker 'lorem.word'}}",
        "color": "{{faker 'color.human'}}",
        "material": "{{faker 'commerce.productMaterial'}}",
        "width": {{int 1 200}},
        "height": {{int 1 200}},
        "weight": {{int 1 5000}},
        "warranty": {{int 0 14}}
      },
      "rating": [
        {{#repeat 15}}
          {
            "stars": {{int 1 5}},
            "message": "{{faker 'hacker.phrase'}}"
          }{{#unless @last}},{{/unless}}
        {{/repeat}}
      ]
    }{{#unless @last}},{{/unless}}
  {{/repeat}}
]
